/**
 * API Client for Metadata Explorer Backend
 */
import axios from 'axios';

// Base URL for API - configure based on environment
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

// Create axios instance with default config
const apiClient = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
  timeout: 60000, // 60 second timeout for long-running queries
});

// Request interceptor for logging
apiClient.interceptors.request.use(
  (config) => {
    console.log(`[API Request] ${config.method?.toUpperCase()} ${config.url}`);
    return config;
  },
  (error) => {
    console.error('[API Request Error]', error);
    return Promise.reject(error);
  }
);

// Response interceptor for error handling
apiClient.interceptors.response.use(
  (response) => {
    console.log(`[API Response] ${response.config.method?.toUpperCase()} ${response.config.url} - ${response.status}`);
    return response;
  },
  (error) => {
    console.error('[API Response Error]', error.response?.data || error.message);
    return Promise.reject(error);
  }
);

/**
 * API Service
 */
const api = {
  // ========== Catalog Endpoints ==========
  
  /**
   * Get all available catalogs
   */
  getCatalogs: async () => {
    const response = await apiClient.get('/api/catalogs');
    return response.data;
  },

  /**
   * Get tables in a specific catalog.schema
   */
  getTablesInCatalog: async (catalog, schema = 'public') => {
    const response = await apiClient.get(`/api/catalogs/${catalog}/tables`, {
      params: { schema },
    });
    return response.data;
  },

  // ========== Table Endpoints ==========
  
  /**
   * Get all tables with metadata
   */
  getTables: async () => {
    const response = await apiClient.get('/api/tables');
    return response.data;
  },

  /**
   * Get sample data from a table
   */
  getTableData: async (catalog, tableName, schema = 'public', limit = 1000) => {
    const response = await apiClient.get(`/api/table-data/${catalog}/${tableName}`, {
      params: { schema, limit },
    });
    return response.data;
  },

  // ========== Metadata Endpoints ==========

  /**
   * Get complete metadata for a table
   */
  getMetadata: async (catalogTable) => {
    const response = await apiClient.get(`/api/metadata/${catalogTable}`);
    return response.data;
  },

  /**
   * Refresh metadata for a table
   */
  refreshMetadata: async (catalogTable, catalog = 'explorer_datasets', schema = 'public') => {
    const response = await apiClient.post(`/api/refresh-metadata/${catalogTable}`, null, {
      params: { catalog, schema },
    });
    return response.data;
  },

  /**
   * Update column aliases
   */
  updateColumnAlias: async (catalogTable, columnName, aliases) => {
    const response = await apiClient.patch(
      `/api/column/${catalogTable}/${columnName}/alias`,
      { aliases }
    );
    return response.data;
  },

  /**
   * Update column metadata (description, column_type, semantic_type)
   */
  updateColumnMetadata: async (catalogTable, columnName, updates) => {
    const response = await apiClient.patch(
      `/api/column/${catalogTable}/${columnName}/metadata`,
      updates
    );
    return response.data;
  },

  // ========== Admin Endpoints ==========

  /**
   * Generate metadata for tables
   */
  generateMetadata: async (tableName = null, catalog = 'explorer_datasets', schema = 'public', forceRefresh = false) => {
    const response = await apiClient.post('/api/admin/generate-metadata', 
      {
        table_name: tableName,
        force_refresh: forceRefresh,
      },
      {
        params: { catalog, schema },
      }
    );
    return response.data;
  },

  /**
   * Get task status
   */
  getTaskStatus: async (taskId) => {
    const response = await apiClient.get(`/api/admin/task-status/${taskId}`);
    return response.data;
  },
};

export default api;
