/**
 * TableSelector Component
 * Searchable dropdown for selecting catalogs and tables
 */
import React, { useState, useEffect } from 'react';
import Select from 'react-select';
import api from '../services/api';
import './TableSelector.css';

const TableSelector = ({ onTableSelect, selectedTable }) => {
  const [catalogs, setCatalogs] = useState([]);
  const [selectedCatalog, setSelectedCatalog] = useState('explorer_datasets');
  const [selectedSchema, setSelectedSchema] = useState('public');
  const [tables, setTables] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Fetch catalogs on mount
  useEffect(() => {
    fetchCatalogs();
  }, []);

  // Fetch tables when catalog or schema changes
  useEffect(() => {
    if (selectedCatalog) {
      fetchTables();
    }
  }, [selectedCatalog, selectedSchema]);

  const fetchCatalogs = async () => {
    try {
      const data = await api.getCatalogs();
      setCatalogs(data.catalogs || []);
    } catch (err) {
      console.error('Error fetching catalogs:', err);
      setError('Failed to load catalogs.');
    }
  };

  const fetchTables = async () => {
    try {
      setLoading(true);
      setError(null);
      
      // Get tables with metadata from DynamoDB
      const data = await api.getTables();
      
      // Filter by selected catalog
      const filteredTables = data.tables.filter(table => 
        table.catalog_table.startsWith(`${selectedCatalog}.`)
      );
      
      setTables(filteredTables || []);
    } catch (err) {
      console.error('Error fetching tables:', err);
      setError('Failed to load tables. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Format catalogs for react-select
  const catalogOptions = catalogs.map((catalog) => ({
    value: catalog,
    label: catalog,
  }));

  // Format tables for react-select
  const tableOptions = tables.map((table) => ({
    value: table.catalog_table,
    label: table.name,
    catalogTable: table.catalog_table,
    schemaStatus: table.schema_status,
    rowCount: table.row_count,
    columnCount: table.column_count,
  }));

  // Custom option renderer to show schema status and stats
  const formatTableOptionLabel = ({ label, schemaStatus, rowCount, columnCount }) => (
    <div className="table-option">
      <span className="table-name">{label}</span>
      {schemaStatus === 'SCHEMA_CHANGED' && (
        <span className="schema-badge changed">Changed</span>
      )}
      <span className="row-count">
        {rowCount?.toLocaleString() || 0} rows â€¢ {columnCount || 0} cols
      </span>
    </div>
  );

  const customStyles = {
    control: (provided) => ({
      ...provided,
      minHeight: '45px',
      borderColor: '#d1d5db',
      boxShadow: 'none',
      '&:hover': {
        borderColor: '#9ca3af',
      },
    }),
    menu: (provided) => ({
      ...provided,
      zIndex: 9999,
    }),
  };

  const selectedCatalogOption = catalogOptions.find((opt) => opt.value === selectedCatalog);
  const selectedTableOption = tableOptions.find((opt) => opt.value === selectedTable);

  return (
    <div className="table-selector">
      {/* Catalog Selector */}
      <div className="selector-group">
        <label htmlFor="catalog-select" className="selector-label">
          Select Catalog
        </label>
        <Select
          id="catalog-select"
          options={catalogOptions}
          value={selectedCatalogOption}
          onChange={(option) => {
            setSelectedCatalog(option?.value || 'explorer_datasets');
            onTableSelect(null); // Clear table selection when catalog changes
          }}
          placeholder="Select catalog..."
          isSearchable
          styles={customStyles}
          className="catalog-select"
          classNamePrefix="catalog-select"
        />
      </div>

      {/* Schema Input (simple text input for now) */}
      <div className="selector-group">
        <label htmlFor="schema-input" className="selector-label">
          Schema
        </label>
        <input
          id="schema-input"
          type="text"
          value={selectedSchema}
          onChange={(e) => {
            setSelectedSchema(e.target.value);
            onTableSelect(null); // Clear table selection when schema changes
          }}
          placeholder="Enter schema name..."
          className="schema-input"
        />
      </div>

      {/* Error Message */}
      {error && (
        <div className="error-message">
          {error}
          <button onClick={fetchTables} className="retry-button">
            Retry
          </button>
        </div>
      )}

      {/* Table Selector */}
      <div className="selector-group">
        <label htmlFor="table-select" className="selector-label">
          Select Table
        </label>
        <Select
          id="table-select"
          options={tableOptions}
          value={selectedTableOption}
          onChange={(option) => onTableSelect(option?.value)}
          placeholder={loading ? 'Loading tables...' : 'Search and select a table...'}
          isLoading={loading}
          isSearchable
          isClearable
          formatOptionLabel={formatTableOptionLabel}
          styles={customStyles}
          className="table-select"
          classNamePrefix="table-select"
        />
      </div>

      {/* Table Count */}
      {tables.length > 0 && (
        <div className="table-count">
          {tables.length} tables in {selectedCatalog}.{selectedSchema}
        </div>
      )}
    </div>
  );
};

export default TableSelector;
